query blogQuery(
  $slug: String!
) {
  contentfulPost(slug: {eq: $slug}) {
    body {
      childMarkdownRemark {
        html
      }
    }
    title {
      title
    }
  }
  site {
    siteMetadata {
      title
    }
  }
}

query workQuery(
  $slug: String!
) {
  contentfulWork(slug: {eq: $slug}) {
    body {
      childMarkdownRemark {
        html
      }
    }
    title {
      title
    }
  }
}

query pageQuery(
  $slug: String!
) {
  contentfulPage(slug: {eq: $slug}) {
    body {
      childMarkdownRemark {
        html
      }
    }
    title {
      title
    }
  }
  site {
    siteMetadata {
      title
    }
  }
}

query GetIntro {
  contentfulIntroduction {
    title
    featuredImage {
      resize(width: 200) {
        src
        height
      }
    }
    createdAt
    content {
      childMarkdownRemark {
        html
      }
    }
  }
  contentfulAuthor {
    name
  }
}

query GetWorkItems {
  site {
    siteMetadata {
      title
    }
  }
  allContentfulWork(sort: {fields: [updatedAt], order: DESC}) {
    edges {
      node {
        slug
        id
        tags
        externalLink
        gitRepoUrl
        isInPrivateRepo
        title {
          title
        }
        body {
          body
          childMarkdownRemark {
            html
          }
        }
        mainImage {
          title
          resize(width: 300) {
            width
            height
            src
          }
          file {
            url
          }
        }
      }
    }
  }
}

query WritingQuery {
  allContentfulPost {
    edges {
      node {
        slug
        id
        createdAt
        title {
          title
        }
        featuredImage {
          resize(width: 300) {
            width
            height
            src
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
    }
  }
}
